

# This function writes the data frame into a csv file
write.csv <- function(ob,filename){
  write.table(ob, filename, quote=FALSE, sep=",", row.names=FALSE)
}

# This function reads the file or the address, and downloads the data, and then ultimately inputs into a data frame
readIt <- function(address) {
  web_page <- readLines(address)

  
  
  ## regex to strip javascript bits and convert raw to csv format
  x1 <- gsub("[\\{\\}\\]]", "", web_page, perl = TRUE)
  x2 <- gsub("[\\[]", "\n", x1, perl = TRUE)
  x3 <- gsub("\"rowSet\":\n", "", x2, perl = TRUE)
  x4 <- gsub(";", ",", x3, perl = TRUE)
  
  # read the resulting csv with read.table()
  nba <- read.table(textConnection(x4), header = F, sep = ",", skip = 3, stringsAsFactors = FALSE)
  
  return(nba)
}

# Assign a file into a list
# After copying and pasting the json object into a json file, make sure to add a semicolon(;) at the end of the file to signify the end of the file.


addSeason <- function(df, season){
  
  x <- rep(season, nrow(df))
  
  df <- cbind.data.frame(df, x)
  
  return(df)
  
}




